import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-email',
  templateUrl: './email.component.html',
  styleUrls: ['./email.component.css']
})
export class EmailComponent implements OnInit {

  data={

    to:"",
    subject:"",
    message:""
  }

  constructor(){}

  ngOnInit(): void {

  }

  doSubmitForm(){
    console.log("try to submit form");

  }
}
<div class="container">

    <div class="card">

        <form (ngSubmit)="doSubmitForm()">

            <h1>Compose Email</h1>
            <mat-form-field class="field" appearance="outline">
                <mat-label>Enter Reciever Mail</mat-label>
                <input name="to" [(ngModel)]="data.to" matInput placeholder="To">
            </mat-form-field>

            <mat-form-field class="field" appearance="outline">
                <mat-label>Enter Subect</mat-label>
                <input name="subject" [(ngModel)]="data.subject" matInput placeholder="Subject">
            </mat-form-field>

            <mat-form-field class="field" appearance="outline">
                <mat-label>Message</mat-label>
                <textarea name="message" [(ngModel)]="data.message" rows="10" matInput placeholder="Enter your message here..."></textarea>
            </mat-form-field>

            <div class="example-button-container text-center">
                <button type="submit" mat-fab extended color="primary">Send</button>
                <button type="reset" mat-fab extended>Reset</button>
            </div>

            {{ data | json}}

        </form>
    </div>
</div>
