import { UpperCasePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { EmailService } from 'src/app/service/email.service';

@Component({
  selector: 'app-email',
  templateUrl: './email.component.html',
  styleUrls: ['./email.component.css']
})
export class EmailComponent implements OnInit {

  data={
    to:"",
    subject:"",
    message:""
  }

  flag=true;

  constructor(private email: EmailService, private snack: MatSnackBar){}

  ngOnInit(): void {

  }

  doSubmitForm(){
    console.log("Data ", this.data);

    if(this.data.to=='' || this.data.subject=='' || this.data.message ==''){

      this.snack.open("FIELDS CAN NOT BE EMPTY!!", "Ok")
    }

    this.flag = false;
    this.email.sendEmail(this.data).subscribe(
      response=>{
        console.log(response);
        this.flag = true
      },
      error=>{
        console.log(error);
        this.flag = true
      }
    )
  }
}
<div class="container">

    <div class="card">

        <form (ngSubmit)="doSubmitForm()">

            <h1>Compose Email</h1>
            <mat-form-field class="field" appearance="outline">
                <mat-label>Enter Reciever Mail</mat-label>
                <input name="to" [(ngModel)]="data.to" matInput placeholder="To">
            </mat-form-field>

            <mat-form-field class="field" appearance="outline">
                <mat-label>Enter Subect</mat-label>
                <input name="subject" [(ngModel)]="data.subject" matInput placeholder="Subject">
            </mat-form-field>

            <mat-form-field class="field" appearance="outline">
                <mat-label>Message</mat-label>
                <textarea name="message" [(ngModel)]="data.message" rows="10" matInput placeholder="Enter your message here..."></textarea>
            </mat-form-field>

            <mat-spinner *ngIf="!flag" style="margin: auto;"></mat-spinner>

            <div class="example-button-container text-center">
                <button [disabled]="true" type="submit" mat-fab extended color="primary">Send</button>
                <button type="reset" mat-fab extended>Reset</button>
            </div>

        </form>
        <!-- {{ data | json}} -->
    </div>
</div>
